var hapi = require("hapi");

var server = new hapi.Server();

server.connection({
    host: "localhost",
    port : 3000
});

server.route({
    method : "GET",
    path : "/",
    handler : function(request, reply){
        reply("Hello this is localhsot");
    }
});
server.route({
    method : "GET",
    path : "/hello",
    handler : function(request, reply){
        reply("Hello World");
    }
});

server.start(function(){
    console.log("Server started at : ", server.info.uri);
});